document.getElementById("home-img-link").addEventListener("click",()=>{let e=document.getElementById("home-img-top"),m=document.getElementById("home-img-bottom");e.classList.contains("home-img-animate-in")?(e.classList.remove("home-img-animate-in"),m.classList.remove("home-img-animate-out"),e.classList.add("home-img-animate-out"),m.classList.add("home-img-animate-in")):(e.classList.remove("home-img-animate-out"),m.classList.remove("home-img-animate-in"),e.classList.add("home-img-animate-in"),m.classList.add("home-img-animate-out"))}),document.getElementById("home-img-bottom").addEventListener("load",()=>{document.getElementById("home-img-outer").style.height=document.getElementById("home-img-bottom").height+"px"});